declare %private function fold-left-helper(
  $input  as item()*,
  $zero   as item()*,
  $action as function(item()*, item(), xs:integer) as item()*,
  $pos    as xs:integer
) as item()* {
  if (empty($input))
  then $zero
  else fold-left-helper(
    tail($input),
    $action($zero, head($input), $pos + 1),
    $action,
    $pos + 1
  )
};

declare function fold-left(
  $input  as item()*,
  $zero   as item()*,
  $action as function(item()*, item(), xs:integer) as item()*
) as item()* {
  fold-left-helper($input, $zero, $action, 1)
};

