  $input  as item()*,
  $zero   as item()*,
  $action as function(item(), item()*, xs:integer) as item()*,
  $pos    as xs:integer
) as item()* {
  if (empty($input))
  then $zero
  else $action(
    head($input),
    fold-right-helper(tail($input), $zero, $action, $pos - 1),
    $pos
  )
